\"><span ><?= __t(\"Actions\"); ?></span></th>\n                        </tr>\n                        </thead>\n                        <tbody>\n                        <?php foreach (\$users as \$user): ?>\n                            <tr data-id=\"<?= \$user->id; ?>\" data-control-url=\"<?= \$this->createUrl(\"user/control\"); ?>\">\n                                <td class=\"select text-center\">\n                                    <label class=\"checkbox-container\">\n                                        <input type=\"checkbox\" id=\"BatchElement_<?= \$user->id ?>\" name=\"\" value=\"<?= \$user->id ?>\" onchange=\"system.control.toggleDeleteBatchButton(); objCheckboxes.toggleCheckBox(this);\">\n                                        <span class=\"checkmark\"></span>\n                                    </label>\n                                </td>\n                                <td class=\"name\">\n                                    <a href=\"<?= \$this->createUrl(\"user/edit\", [\"id\" => \$user->id]); ?>\">\n                                        <?= CHtml::encode(isset(\$user->name) && mb_strlen(\$user->name) ? \$user->name : \$user->email); ?>\n                                    </a>\n                                </td>\n                                <td class=\"role text-center\">\n                                    <?php if (isset(\$user->role)): ?>\n                                        <span class=\"tag label-<?= \$user->role ?>\">\n                                        <?= CHtml::encode(\$user->role->name); ?>\n                                    </span>\n                                    <?php else: ?>\n                                        <em>Not set</em>\n                                    <?php endif; ?>\n                                </td>\n                                <td class=\"actions\">\n                                    <?php if (\$user->id != Yii::app()->user->id): ?>\n                                        <a role=\"button\" href=\"#del\" aria-label=\"<?= __t(\"Delete\") ?>\" title=\"<?= __t(\"Delete\"); ?>\" onclick=\"system.control.del(<?= \$user->id; ?>, '<?= __t(\"WARNING! ALL DATA RELATED TO THIS USER WILL BE DELETED!\"); ?>');\"><i class=\"icon icon-delete\"></i></a>\n                                    <?php endif; ?>\n                                </td>\n                            </tr>\n                        <?php endforeach; ?>\n                        </tbody>\n                    </table>\n                </div>\n                <div class=\"padding-horizontal-36\">\n                    <?= \$this->renderPartial(\"application.views.layouts.partial.pagination\", [\"p\" => \$p, \"url\" => \"user/index\", \"params\" => []]); ?>\n                </div>\n            <?php else: ?>\n                <div class=\"no-data-found\"><?= __t(\"No users found.\"); ?></div>\n            <?php endif; ?>\n        </div>\n    </div>\n</div>\n\n<script>\n    \$(function () {\n        system.sortTableListener(\"<?= CookieManager::PAGE_USERS; ?>\");\n    });\n</script>\n\n<?php if (\$running && \$this->_system->ajax_update_period): ?>\n    <script>\n        \$(function () {\n            system.refreshStatus(\"<?= \$this->createUrl(\"user/ldapConvertStatus\"); ?>\", <?= \$this->_system->ajax_update_period; ?>);\n            system.spinner.start();\n        });\n    </script>\n<?php endif; ");
