                        endpoint: 'resources/plugins/fineuploader-3.7.1/php/upload.php'
                    },
                    deleteFile: {
                        enabled: true,
                        forceConfirm: true,
                        endpoint: 'resources/plugins/fineuploader-3.7.1/php/delete_file.php'
                    },
                    autoUpload: true,
                    multiple: true,
                    validation: {
                        allowedExtensions: var_allowedExts,
                        //allowedExtensions: ['DWG', 'DWF', 'DXF', 'doc', 'docx', 'xls', 'xlsx', 'ppt', 'pptx', 'txt', 'pdf', 'jpeg', 'jpg', 'gif', 'png', 'bmp', 'tif', 'pps', 'ppsx', 'mp3', 'mp4', 'mpg', 'mpeg', 'wmv', 'avi', 'ods', 'ics', 'htf'],
                        sizeLimit: 104857600 // 50 MB
                    }
                }).on('submit', function (event, id, fileName) {
                    if (localStorage.getItem('newFile') === fileName) {
                        localStorage.removeItem('newFile');
                        return true;
                    }
                    $(".qq-upload-list").append('<li id="li-compressing-' + id + '" class=" qq-upload-processing"> <div class="qq-progress-bar"></div><span class="qq-upload-file">' + fileName + ' - <i class="fa fa-file-archive-o"></i> Compressing </span><span class="qq-upload-spinner"></span></li>');

                    //spinner start
                    let fileRef = $('#jquery-wrapped-fine-uploader');
                    let fileSize = fileRef.fineUploader('getSize', id);
                    function parseFile(file, callback) {
                        let chunkSize = 1024 * 1024; // bytes - 1MiB
                        let offset = 0;
                        let self = this; // we need a reference to the current object
                        let chunkReaderBlock = null;

                        let readEventHandler = function (evt) {
                            if (evt.target.error == null) {
                                offset += evt.target.result.length;
                                callback(evt.target.result); // callback for handling read chunk
                            } else {
                                $("#li-compressing-" + id).hide();
                                $(".qq-upload-list").append('<li id="li-compressing-error-' + id + '" class=" qq-upload-processing-error"> <div class="qq-progress-bar"></div><span class="qq-upload-file">' + fileName + ' - <i class="fa fa-file-archive-o"></i> Read Error. Please re-try.</span></li>');
                                //alert("Read error: " + evt.target.error);

                                return;
                            }
                            if (offset >= fileSize) {
                                //alert("Done reading file");
                                return;
                            }

                            // of to the next chunk
                            chunkReaderBlock(offset, chunkSize, file);
                        }

                        chunkReaderBlock = function (_offset, length, _file) {
                            let r = new FileReader();
                            let blob = _file.slice(_offset, length + _offset);
                            r.onload = readEventHandler;
                            r.readAsBinaryString(blob);
                        }

                        // now let's start the read with the first block
                        chunkReaderBlock(offset, chunkSize, file);
                    }

                    let deflator = new pako.Deflate({gzip: true, level: 9});
                    let totalBytes = 0;

                    function addChunkToDeflator(nextChunk) {
                        //alert("deflator called")
                        let charData = nextChunk.split('').map(function (x) {
                            return x.charCodeAt(0);
                        });
                        let myArr = new Uint8Array(charData);
                        totalBytes += nextChunk.length;
                        //alert(nextChunk.length);
                        if (totalBytes >= fileSize) {
                            deflator.push(myArr, true);
                            let compressed = deflator.result;
                            //alert(compressed.length);
                            if (compressed.length > fileSize) {
                                localStorage.setItem('newFile', fileName);
                                fileRef.fineUploader('addFiles', fileRef.fineUploader('getFile', id));

                                $("#li-compressing-" + id).hide();
                                //spinner end
                                return 1;
                            } else {
                                let gzipFile = new File([compressed], fileName, {type: "application/octet-stream"});
                                localStorage.setItem('newFile', fileName);
                                fileRef.fineUploader('addFiles', gzipFile);

                                $("#li-compressing-" + id).hide();
                                //spinner end
                                return 0;
                            }
                        } else {
                            deflator.push(myArr, false);
                        }
                    }
                    parseFile(fileRef.fineUploader('getFile', id), addChunkToDeflator);
                    return false;
                }).on('complete', function (event, id, fileName, responseJSON) {
                    if (responseJSON.success) {
                        $(this).append('<input type="hidden" name="file_name[]" id="file_name_' + id + '" value="' + fileName + '" >');
                    }
                }).on('submitDelete', function (event, id, fileName, responseJSON) {
                    $("#file_name_" + id).remove();
                })
                $(".qq-upload-delete").click(function () {
                    var id = $(this).prop('id').split('_')[1];
                    var file_name = $("#file_name_" + id).prop('value');
                    if (confirm('Are you sure you want to delete ' + file_name + '?')) {
                        $("#jquery-wrapped-fine-uploader_" + id).remove();
                        $("#file_name_" + id).remove();

                        $("#div_doc_keywords_" + id).remove();
                    }

                });
            });
        </script>
