        if (!function_exists('num2month')) {
            function num2month($num = 0, $type = 1)
            {
                $month[1] = array('Januari', 'Februari', 'Maret', 'April', 'Mei', 'Juni', 'Juli', 'Agustus', 'September', 'Oktober', 'November', 'Desember');
                $month[2] = array('Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Agust', 'Sep', 'Oct', 'Nov', 'Dec');
                $month[3] = array('Jan', 'Feb', 'Mar', 'Apr', 'Mei', 'Jun', 'Jul', 'Agu', 'Sep', 'Okt', 'Nov', 'Des');
                $month[4] = array('I', 'II', 'III', 'IV', 'V', 'VI', 'VII', 'VIII', 'IX', 'X', 'XI', 'XII');
                return $month[$type][$num - 1];
            }
        }
        if (!function_exists('quotedstr')) {
            function quotedstr($str)
            {
                $str = str_replace("'", "''", $str);
                return "'" . $str . "'";
            }
        }
        if (!function_exists('match_key')) {
            function match_key($data, $key)
            {
                $data_upper = trim(strtoupper($data));
                $key_upper = trim(strtoupper($key));
                $pos_start = strrpos($data_upper, $key_upper);
                $result = $data;
                $mark_start = '<span class="filteredKey">';
                $mark_end = '</span>';
                if ($pos_start or $key_upper == substr($data_upper, 0, strlen($key_upper))) {
                    $pos_end = $pos_start + strlen($key_upper);
                    $result = substr_replace($data, $mark_start, $pos_start, 0);
                    $result = substr_replace($result, $mark_end, $pos_end + strlen($mark_start), 0);
                }
                return $result;
            }
        }
