        function getMenuBoxed()
        {
            $_this =& get_Instance();
            $uri_string = preg_replace('/[^a-z0-9]/i', '_', uri_string());
            $_this->db->where('lower("URL") = ' . quotedstr($uri_string) . '');
            $_this->db->where('am."idAppMenu" IN 
							(SELECT "fidMenus"
									FROM public."UsersAccess"
									WHERE "fkUsers" = \'' . $_this->session->userdata('pkUsers') . '\')');
            $_this->db->order_by('OrderBy', 'ASC');
            $get_parent = $_this->db->get('public.AppMenus am');
            $menu_parent = array();
            $menu_child_list = array();
            if ($get_parent->num_rows() > 0 && $uri_string != '') {
                $menu_parent = $get_parent->row_array();
                $id_menu = $menu_parent['idAppMenu'];
            } else {
                $id_menu = 0;
            }
            $_this->db->where('am.fidAppMenu = ' . $id_menu . '');
            $_this->db->where('am."idAppMenu" IN 
								(SELECT "fidMenus"
										FROM public."UsersAccess"
										WHERE "fkUsers" = \'' . $_this->session->userdata('pkUsers') . '\')');
            $_this->db->order_by('OrderBy', 'ASC');
            $get_child = $_this->db->get('public.AppMenus am');
            if ($get_child->num_rows() > 0) {
                $menu_child_list = $get_child->result_array();
            }
            $default_menu_url = 'main/menu_boxed';
            $data = array('content' => $default_menu_url, 'title' => 'Settings', 'uri_string' => $uri_string, 'menu_child_list' => $menu_child_list);
            $_this->load->view('tpl', $data);
        }
        function get_accept_header()
        {
            $CI =& get_Instance();
            $accept_request = $CI->input->get_request_header('accept', TRUE);
            $arr = explode(",", $accept_request, 2);
            $first = $arr[0];
            return strtolower($first);
        }
        function validate_input($str = '', $params = array())
        {
            $_this =& get_Instance();
            $name = isset($params['name']) ? $params['name'] : 'undefined';
            if (isset($params['required'])) {
                if ($params['required']) {
                    if ($str == '') {
                        error('kolom ' . $name . ' belum di isi');
                    }
                }
            }
            if ($str) {
                if (isset($params['type'])) {
                    if ($params['type']) {
                        if ($params['type'] == 'string' || $params['type'] == 'varchar' || $params['type'] == 'text') {
                            if (!is_string($str)) {
                                error('kolom ' . $name . ' harus berisi tipe ' . $params['type']);
                            }
                        }
                        if ($params['type'] == 'number' || $params['type'] == 'float' || $params['type'] == 'int' || $params['type'] == 'integer') {
                            if (!is_numeric($str)) {
                                error('kolom ' . $name . ' harus berisi tipe ' . $params['type']);
                            }
                        }
                        if ($params['type'] == 'date') {
                            if (!preg_match("/^[0-9]{4}(0[1-9]|1[0-2])(0[1-9]|[1-2][0-9]|3[0-1])\$/", getSQLDate($str))) {
                                error('kolom ' . $name . ' invalid ' . $params['type'] . ' format');
                            }
                            $year = substr(getSQLDate($str), 0, 4);
                            // returns "2019"
                            $month = substr(getSQLDate($str), 4, 2);
                            // returns "12"
                            $day = substr(getSQLDate($str), 6, 2);
                            // returns "31"
                            if (!checkdate($month, $day, $year)) {
                                error('params ' . $name . ' invalid ' . $params['type'] . ' format' . ' y:' . $year . ' m:' . $month . ' d:' . $day);
                            }
                        }
                    }
                }
            }
            if (isset($params['maxlength'])) {
                if ($params['maxlength']) {
                    if (strlen($str) > $params['maxlength']) {
                        error('kolom ' . $name . ' melebihi panjang maximum ' . $params['maxlength']);
                    }
                }
            }
            if (isset($params['default'])) {
                if ($str == '') {
                    $str = $params['default'];
                }
            }
            return $str;
        }
        if (!function_exists('checkAccess')) {
            function checkAccess($priv = false, $idUsers = 0)
            {
                $_this =& get_Instance();
                $_this->load->model('users/users_modul_model');
                $_this->load->model('users/users_modul_privilege_model');
                $module = $_this->users_modul_model->get(array('SpecialVar' => $priv));
                $hasil = 0;
                $where = array();
                $where['fidUsers'] = $idUsers;
                if ($priv) {
                    $where['fidUsersModul'] = $module['idUsersModul'] ?: 0;
                }
                $where['Status'] = 1;
                $_this->users_modul_privilege_model->set_where($where);
                $hasil = $_this->users_modul_privilege_model->count() > 0;
                return $hasil;
            }
        }
        if (!function_exists('checkPriv')) {
            function checkPriv()
            {
                $_this =& get_Instance();
                $sess = $_this->session->userdata('users');
                $_this->load->model('users/users_modul_model');
                $_this->users_modul_model->set_order(array('OrderBy', 'ASC'));
                $module = $_this->users_modul_model->get_list();
                $config = [];
                foreach ($module->result_array() as $row) {
                    $config['priv' . $row['SpecialVar']] = checkAccess($row['SpecialVar'], $sess['idUsers']);
                }
                return $config;
            }
        }
        if (!function_exists('genNumberRange')) {
            function genNumberRange($min, $max, $quantity)
            {
                $numbers = range($min, $max);
                shuffle($numbers);
                $arr = array_slice($numbers, 0, $quantity);
                $arrval = '';
                for ($i = 0; $i < count($arr); $i++) {
                    $arrval .= ',' . $arr[$i];
                }
                return ltrim($arrval, ',');
            }
        }
